mapper.select:
SELECT (SELECT COUNT (D.ID)
          FROM DONATIONS D JOIN CAMPAIGNS C ON C.ID = D.ID_CAMPAIGN
          WHERE 1 = 1
          AND  D.ID_EMPLOYEE_TEAM = #{monthly.idEmployee}
          AND C.ID_OSC = #{monthly.idOSC}
          AND  MONTH(D.CREATE_DATE) = #{monthly.monthNumber}
          AND YEAR(D.CREATE_DATE)= #{monthly.yearNumber}) TOTALDONATIONS,
       (SELECT SUM (D.AMOUNT)
          FROM DONATIONS D JOIN CAMPAIGNS C ON C.ID = D.ID_CAMPAIGN
          WHERE 1 = 1
          AND  D.ID_EMPLOYEE_TEAM = #{monthly.idEmployee}
          AND C.ID_OSC = #{monthly.idOSC}
          AND  MONTH(D.CREATE_DATE) = #{monthly.monthNumber}
          AND YEAR(D.CREATE_DATE)= #{monthly.yearNumber}) TOTALAMOUNTDONATIONS,
       (SELECT COUNT (D.ID_TYPE_PAY)
          FROM DONATIONS D JOIN CAMPAIGNS C ON C.ID = D.ID_CAMPAIGN
          WHERE 1 = 1
          AND  D.ID_EMPLOYEE_TEAM = #{monthly.idEmployee}
          AND C.ID_OSC = #{monthly.idOSC}
          AND  MONTH(D.CREATE_DATE) = #{monthly.monthNumber}
          AND YEAR(D.CREATE_DATE)= #{monthly.yearNumber}
          AND D.ID_TYPE_PAY =1) CREDITTYPE,
       (SELECT SUM (DR.PRODUCTIVE_HOURS)
          FROM DAILY_RECORDS DR JOIN CAMPAIGNS C ON C.ID = DR.ID_CAMPAIGN
          WHERE 1 = 1
          AND  DR.ID_EMPLOYEE_TEAM = #{monthly.idEmployee}
          AND C.ID_OSC = #{monthly.idOSC}
          AND  MONTH(DR.CREATE_DATE) = #{monthly.monthNumber}
          AND YEAR(DR.CREATE_DATE)= #{monthly.yearNumber}) TOTALPRODUCTIVEHOURS,
       (SELECT SUM (DR.NON_PRODUCTIVE_HOURS)
          FROM DAILY_RECORDS DR JOIN CAMPAIGNS C ON C.ID = DR.ID_CAMPAIGN
          WHERE 1 = 1
          AND  DR.ID_EMPLOYEE_TEAM = #{monthly.idEmployee}
          AND C.ID_OSC = #{monthly.idOSC}
          AND  MONTH(DR.CREATE_DATE) = #{monthly.monthNumber}
          AND YEAR(DR.CREATE_DATE)= #{monthly.yearNumber}) TOTALNONPRODUCTIVEHOURS
